mapper.select:
SELECT COMMENTSTATUS FROM TREVIEWCOMMENT WHERE TREVCID IN ( SELECT PARENTID FROM TREVIEWCOMMENT START WITH TREVCID IN
#{trevcId}
CONNECT BY PRIOR PARENTID = TREVCID ) AND COMMENTSTATUS IN('CMT002', 'CMT004')

SELECT * FROM ( SELECT c.TREVCID, c.TREVID, c.TOPPARENTID, c.PARENTID, c.PARENTNICKNAME, c.MEMID, c.MEMNICK, c.TREVCTEXT, c.TREVDEPTH, c.COMMENTSTATUS, c.TREVCREGDATE, c.TREVDUPDATE, ROW_NUMBER() OVER( ORDER BY
c.TREVCREGDATE DESC c.TREVCREGDATE ASC c.TREVCREGDATE DESC
) AS rn FROM TREVIEWCOMMENT c WHERE 1=1
AND c.COMMENTSTATUS IN ('CMT001', 'CMT003', 'CMT005')
AND c.COMMENTSTATUS IN ('CMT002', 'CMT004')
AND c.TREVCTEXT LIKE '%' || #{dto.search} || '%'
AND c.TREVID = #{dto.search}
AND c.MEMNICK LIKE '%' || #{dto.search} || '%'
AND (c.TREVCTEXT LIKE '%' || #{dto.search} || '%' OR c.MEMNICK LIKE '%' || #{dto.search} || '%')
) c WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{pageRowCount}
