mapper.select:
SELECT * FROM ( SELECT r.TREVID, r.TRIPID, r.MEMID, r.TREVSTATUS, r.TREVTITLE, r.TREVCONTENT, r.TREVRATING, r.TREVREGDATE, r.TREVUPDATE, r.TREVCOUNT, ROW_NUMBER() OVER( ORDER BY
r.TREVREGDATE DESC r.TREVREGDATE ASC r.TREVCOUNT DESC r.TREVRATING DESC r.TREVREGDATE DESC
) AS rn FROM TREVIEW r WHERE r.TREVSTATUS IN ('CMT001', 'CMT003')
AND r.MEMID = #{memId}
AND r.TREVTITLE LIKE '%' || #{dto.search} || '%'
AND r.TREVCONTENT LIKE '%' || #{dto.search} || '%'
AND (r.TREVTITLE LIKE '%' || #{dto.search} || '%' OR r.TREVCONTENT LIKE '%' || #{dto.search} || '%')
) r LEFT JOIN TReviewImage i ON r.TREVID = i.TREVID WHERE r.rn BETWEEN #{offset} + 1 AND #{offset} + #{pageRowCount} ORDER BY
r.TREVREGDATE DESC r.TREVREGDATE ASC r.TREVCOUNT DESC r.TREVRATING DESC r.TREVREGDATE DESC

mapper.update:
UPDATE TREVIEW
SET TREVCOUNT = TREVCOUNT + 1
WHERE TREVID = #{trevId}

UPDATE TREVIEW
SET TREVSTATUS = 'CMT002', TREVUPDATE = CURRENT_TIMESTAMP
WHERE TREVID = #{trevId}

UPDATE TREVIEW
SET TREVSTATUS = 'CMT003'
WHERE TREVID = #{trevId}
