mapper.update:
UPDATE TREVIEWCOMMENT
SET TOPPARENTID = #{commentId}
WHERE TREVCID = #{commentId}

UPDATE TREVIEWCOMMENT
SET TREVCTEXT = #{content}, TREVDUPDATE = sysdate
WHERE TREVCID = #{commentId}

UPDATE TREVIEWCOMMENT
SET COMMENTSTATUS = 'CMT003'
WHERE TREVCID = #{commentId}

mapper.select:
	SELECT TO_CHAR(TREVDUPDATE, 'YYYY-MM-DD HH24:MI') AS updatedDate
    FROM TREVIEWCOMMENT
    WHERE TREVCID = #{commentId}
